import React, { Component } from 'react' 
import TextField from 'material-ui/TextField';
import SelectField from 'material-ui/SelectField';
import MenuItem from 'material-ui/MenuItem';
import Axios from 'axios';


class Search extends Component {
    state = {
        searchText: '',
        amount: 15,
        apiUrl: 'https://pixabay.com/api/',
        apiKey: '12755462-8c4bf759c09a3311f0a543fad',
        images: [] 
    };

    onTextChange = (e) => {
        this.setState({[e.target.name]: e.target.value},() => {
            Axios.get(`${this.state.apiUrl}/?key=${this.state.apiKey}&q=${this.state.searchText}&image_type=photo&
            per_page=${this.state.amount}&safesearch=true`)
                .then(res => this.setState({images: res.data.hits}))
                .catch(err => console.log(err)); 
        });
    };

    render() {
        console.log(this.state.images);
        return (
            <div>
                <TextField 
                    name = "searchText"
                    value = {this.state.searchText}
                    onChange = {this.onTextCange}
                    floatingLabelText = "Search Images Here"
                    fullWidth = {true}
                />
                <SelectField
                    name = "amount"
                    floatingLabelText="Amount"
                    value = {this.state.amount}
                    onChange = {this.onAmountChange}
                >
                    <MenuItem value = {5} primaryText = "2" />
                    <MenuItem value = {20} primaryText = "2" />
                    <MenuItem value = {30} primaryText = "2" />
                    <MenuItem value = {40} primaryText = "2" />
                    <MenuItem value = {50} primaryText = "2" />
                </SelectField>


            </div>
        )
    }
}

export default Search;
